import React, { useState, useEffect } from "react";
import CourseActive from "./components-sm/CourseActive";
import Welcome from "./components-sm/Welcome";
import Recommended from "./components-sm/Recommended";
import axios from "axios";

const Dashboard = () => {

    const [userData, setUserData] = useState(null);
    let [courses, setCourses] = useState([{}]);

    const fetchUserData = async () => {
        console.log("fetchUserdata called");

        try {
            const res = await axios.get('/dashboard');

            const data = res.data;
            if (res.status === 401) {
                window.alert("unAuthorized access");
            } else {
                // setUserData(data);
                data.forEach(courseId => {
                    getCourse(courseId);
                    // console.log(courseId);
                });
            }
        } catch (error) {
            window.alert("An error occured!")
            console.log(error.response.data);
        }



    }
    useEffect(() => {
        fetchUserData();
    }, []);

    const getCourse = async (id) => {
        const res = await axios.get(`/course/${id}`);
        setCourses(...courses, res.data.name);
        console.log(courses);
        
        return true;
    }

    return (<>
        <Welcome />
        <CourseActive />
        <Recommended />
        {<button onClick={() => { fetchUserData() }}>Click me </button>}
        {/* {courses && <div>{courses.map(course => {
            return "hello";
        })}</div>} */}
        {<div>{courses}</div>}
    </>);
}

export default Dashboard;
