import React, { useState, useEffect } from "react";
import CourseActive from "./components-sm/CourseActive";
import Welcome from "./components-sm/Welcome";
import Recommended from "./components-sm/Recommended";
import axios from "axios";

const Dashboard = () => {

    const [userData, setUserData] = useState(null);
    let courses = [];

    const fetchUserData = async () => {
        console.log("fetchUserdata called");

        try {
            const res = await axios.get('/dashboard');

            const data = res.data;
            if (res.status === 401) {
                window.alert("unAuthorized access");
            } else {
                setUserData(data);
            }
        } catch (error) {
            window.alert("An error occured!")
            console.log(error.response.data);
        }
    }
    useEffect(() => {
        fetchUserData();
        getCourse();
    }, []);

    const getCourse = () => {

        //Now getting course data

        userData.forEach(async (course) => {
            const res = await axios.get(`/course/${course}`);
            courses.push(res.data.name);
            console.log(courses);
        });
        

        // return <div key={id}>{id}</div>;
    }

    return (<>
        <Welcome />
        <CourseActive />
        <Recommended />
        {<button onClick={() => {fetchUserData()}}>Click me </button>}
        {userData && <div>{courses.map((courseName) => {
            return <div>{courseName}</div>;
        })}</div>}
    </>);
}

export default Dashboard;
