import React, {useState, useEffect} from "react";
import CourseActive from "./components-sm/CourseActive";
import Welcome from "./components-sm/Welcome";
import Recommended from "./components-sm/Recommended";
import axios from "axios";

const Dashboard = () => {

    const [userData, setUserData] = useState(null);
    setUserData("name");

    useEffect(() => {
        const fetchUserData = async () => {
            console.log("fetchUserdata called");
    
            try {
                const res = await axios.get('/dashboard');
    
                const data = res.data.msg;
                console.log(res.data);
                if (res.status === 401) {
                    window.alert("unAuthorized access attempt!");
                    console.log(data);
                } else {
                    setUserData(data);
                }
            } catch (error) {
                window.alert("Some bad happend!")
                console.log(error.response.data);
            }
        }
    }, []);


    return (<>
        <Welcome />
        <CourseActive />
        <Recommended />
        {/* {<button onClick={fetchUserData}>Click me </button>} */}
        {userData && <div>{userData}</div>}
    </>);
}

export default Dashboard;
