import React, {useState} from 'react';
import { Link } from 'react-router-dom';

const Login = () => {

    const [user, setUser] = useState({
        username: "",
        password: ""
    });

    const handleChange = (event) => {
        setUser({username: event.key});
        console.log(user.username);
    }

    return (<>
        <section>
            <div className="login-holder">
                <div className="login">
                    <h4>Log in to Coursely</h4><a className="google-btn btn" href="#"><i className="fa-brands fa-google me-2"></i>Continue with Google</a>
                    <div><input type="text" onKeyDown={handleChange} placeholder="Email" name='email' value={user.email}/></div>
                    <div><input type="password" placeholder="password" name='password' /></div><a className="btn my-btn" href="#">Login</a>
                    <div><span>Don't have an Account?&nbsp;<strong><Link className="custom-link" to={"/register"}>Sign up</Link></strong></span></div>
                </div>
            </div>
        </section>
    </>);
}

export default Login;
