import React, { useState, useEffect } from "react";
import CardActive from "./components-sm/CardActive";
import Welcome from "./components-sm/Welcome";
import Recommended from "./components-sm/Recommended";
import axios from "axios";

const Dashboard = () => {

    const [userData, setUserData] = useState(null);

    const fetchUserData = async () => {
        console.log("fetchUserdata called");

        try {
            const res = await axios.get('/dashboard');

            const data = res.data;
            if (res.status === 401) {
                window.alert("unAuthorized access");
            } else {
                data.forEach(async id => {
                    const res = await axios.get('/course/' + id);
                    const newCourse = res.data.name;
                    setUserData(prevValue => ([prevValue, newCourse]));
                })
            }
        } catch (error) {
            window.alert("An error occured!")
            console.log(error.response.data);
        }



    }
    useEffect(() => {
        fetchUserData();
    }, []);

    return (<>
        <Welcome />
        <section>
            <div className="container">
                <div className="row">
                    <CardActive
                        course="singh DSA with JAVA &amp; C++"
                        chapter="1. Welcome to the world of web design/development"
                        module="4"
                    />
                    <CardActive
                        course="ashish DSA with JAVA &amp; C++"
                        chapter="1. Welcome to the world of web design/development"
                        module="4"
                    />
                    <CardActive
                        course="Advanced DSA with JAVA &amp; C++"
                        chapter="1. Welcome to the world of web design/development"
                        module="4"
                    />
                </div>
            </div>
        </section>
        <Recommended />
        {<button onClick={() => { fetchUserData() }}>Click me </button>}
        {/* {courses && <div>{courses.map(course => {
            return "hello";
        })}</div>} */}
        {<div>{userData.map(ele => (<div>{ele}</div>))}</div>}
    </>);
}

export default Dashboard;
