import React, { useState, useEffect } from "react";
import CourseActive from "./components-sm/CourseActive";
import Welcome from "./components-sm/Welcome";
import Recommended from "./components-sm/Recommended";
import axios from "axios";

const Dashboard = () => {

    const [userData, setUserData] = useState(null);

    const fetchUserData = async () => {
        console.log("fetchUserdata called");

        try {
            const res = await axios.get('/course');

            const data = res.data.data;
            console.log(res.data);
            if (res.status === 401) {
                window.alert("unAuthorized access");
            } else {
                setUserData(data);
            }
        } catch (error) {
            window.alert("An error occured!")
            console.log(error.response.data);
        }
    }
    useEffect(() => {
        fetchUserData();
    }, []);

    const renderCard = (obj) => {
        console.log(obj);
        return <div>{obj.creator}</div>;
    }

    const arr = [1,2,3,4,5,6];

    return (<>
        <Welcome />
        <CourseActive />
        <Recommended />
        {<button onClick={() => setUserData("hello")}>Click me </button>}
        {arr && <div>{arr.map((user) => (
            <div>{user}</div>
        ))}</div>}
    </>);
}

export default Dashboard;
