import React, { useState, useEffect } from "react";
import CourseActive from "./components-sm/CourseActive";
import Welcome from "./components-sm/Welcome";
import Recommended from "./components-sm/Recommended";
import axios from "axios";

const Dashboard = () => {

    const [userData, setUserData] = useState(null);

    const fetchUserData = async () => {
        console.log("fetchUserdata called");

        try {
            const res = await axios.get('/dashboard');

            const data = res.data;
            if (res.status === 401) {
                window.alert("unAuthorized access");
            } else {
                data.forEach(async element => {
                    const res = await axios.get('/course/' + element);
                    const newCourse = res.data.name;
                    setUserData([userData, newCourse]);
                    console.log(userData);
                })
            }
        } catch (error) {
            window.alert("An error occured!")
            console.log(error.response.data);
        }



    }
    useEffect(() => {
        fetchUserData();
    }, []);

    return (<>
        <Welcome />
        <CourseActive />
        <Recommended />
        {<button onClick={() => { fetchUserData() }}>Click me </button>}
        {/* {courses && <div>{courses.map(course => {
            return "hello";
        })}</div>} */}
        {<div>{userData}</div>}
    </>);
}

export default Dashboard;
