import React, { useState, useEffect } from "react";
import CardActive from "./components-sm/CardActive";
import Welcome from "./components-sm/Welcome";
import Recommended from "./components-sm/Recommended";
import axios from "axios";

const Dashboard = () => {

    const [userData, setUserData] = useState([]);

    const fetchUserData = async () => {
        console.log("fetchUserdata called");

        try {
            const res = await axios.get('/dashboard');

            const data = res.data;
            if (res.status === 401) {
                window.alert("unAuthorized access");
            } else {
                data.forEach(async id => {
                    const res = await axios.get('/course/' + id);
                    const { name, _id } = res.data;
                    const [{ lessonName }] = res.data.lessons;
                    setUserData(prevValue => {
                        return [
                            ...prevValue,
                            {
                                _id,
                                name,
                                lessonName
                            }
                        ];
                    });
                })
            }
        } catch (error) {
            window.alert("An error occured!")
            console.log(error.response.data);
        }



    }
    useEffect(() => {
        fetchUserData();
    }, []);

    return (<>
        <Welcome />
        <section>
            <div className="container">
                {userData && <div className="row">{userData.map(course => {
                    return <CardActive
                        key={course._id}
                        course={course.name}
                        chapter={course.lessonName}
                        module="4"
                    />;
                })}</div>}
            </div>
        </section>
        <Recommended />
    </>);
}

export default Dashboard;
