import React, { useState, useEffect } from "react";
import CourseActive from "./components-sm/CourseActive";
import Welcome from "./components-sm/Welcome";
import Recommended from "./components-sm/Recommended";
import axios from "axios";

const Dashboard = () => {

    const [userData, setUserData] = useState(null);

    const fetchUserData = async () => {
        console.log("fetchUserdata called");

        try {
            const res = await axios.get('/dashboard');

            const data = res.data;
            // console.log(res.data);
            if (res.status === 401) {
                window.alert("unAuthorized access");
            } else {
                setUserData(data);
            }
        } catch (error) {
            window.alert("An error occured!")
            console.log(error.response.data);
        }
    }
    useEffect(() => {
        fetchUserData();
    }, []);

    const getCourse = async (id) => {
        // const res = await axios.get('/course/${id}');
        console.log('/course/${id}');
        return "/course/${id}";
    }

    return (<>
        <Welcome />
        <CourseActive />
        <Recommended />
        {<button onClick={() => {fetchUserData()}}>Click me </button>}
        {userData && <div>{userData.map((courseId) => {
            return <div>{getCourse(courseId)}</div>
        })}</div>}
    </>);
}

export default Dashboard;
