import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Register = () => {

    const [user, setUser] = useState({
        name: "",
        username: "",
        password: ""
    })
    const [change, setChange] = useState(null);

    const handleInput = (e) => {
        const { target, value } = e.target;
        setChange({...user, [target]: value})
    }

    return (<>
        <section><div className="login-holder">
            <div className="login">
                <h4>Sign Up for Coursely</h4><a className="google-btn btn" href="#"><i className="fa-brands fa-google me-2"></i>Continue with Google</a>
                <div><input type="text" onChange={handleInput} name='name' placeholder="Name" value={user.name} /></div>
                <div><input type="text" onChange={handleInput} name='username' placeholder="Email" value={user.email} /></div>
                <div><input type="password" onChange={handleInput} name='password' placeholder="password" value={user.password} /></div><a className="btn my-btn" href="#">Sign Up</a>
                <div><span>Have an Account?&nbsp;<strong><Link className="custom-link" to={"/login"}>Login</Link></strong></span></div>
            </div>
        </div>
        </section>
    </>);
}

export default Register;
