import React from 'react';
import { Link } from 'react-router-dom';

const Login = () => {
    return (<>
        <section>
            <div className="login-holder">
                <div className="login">
                    <h4>Log in to Coursely</h4><a className="google-btn btn" href="#"><i className="fa-brands fa-google me-2"></i>Continue with Google</a>
                    <form method='POST' action='localhost:3000/login'>
                        <div><input type="text" placeholder="Email" /></div>
                        <div><input type="password" placeholder="password" /></div><button type='submit' className="btn my-btn">Login</button>
                        <div><span>Don't have an Account?&nbsp;<strong><Link className="custom-link" to={"/register"}>Sign up</Link></strong></span></div>
                    </form>
                </div>
            </div>
        </section>
    </>);
}

export default Login;
