import React from 'react';
import { Link } from 'react-router-dom';

const CardCourse = (Props) => {

    const { course, creator, star, rating } = Props;
    const starInit = () => {
        return <i className="fa-solid fa-star"></i>;
    }

    return (<>
        <div className="my-card">
            <Link className="custom-link" to={"/course-detail"} state={{data: Props}}>
                <div className="card"><img alt="courseImage" className="card-img-top w-100 d-block fit-cover" src={require("../../img/web-development.jpg")} />
                    <div className="card-body">
                        <h4 className="card-title card-p-h4 card-h4">{course}</h4>
                        <p className="card-text card-p-h4">{creator}</p>
                        <div style={{ color: "var(--han-blue)" }}>
                            {starInit()}
                            {starInit()}
                            {starInit()}
                            {starInit()}
                            <p className="card-text" style={{ color: "#000" }}>&nbsp;({rating})</p>
                        </div>
                        <p className="fw-bold price card-p-h4">Free</p>
                    </div>
                </div>
            </Link>
        </div>
    </>);
}

export default CardCourse;
